mapper.select:
SELECT f.id, f.state, f.remark, f.auditTime, f.applyTime, f.auditor_id, f.score, f.image, sd.id as sd_id,sd.title as sd_title FROM userfile f LEFT JOIN systemdictionaryitem sd ON f.fileType_id = sd.id WHERE applier_id = #{logininfoId}
AND fileType_id is NOT NULL
AND fileType_id is NULL

sql.where:

	AND f.applyTime >= #{beginDate}


	AND f.applyTime <=#{endDate}


	AND f.state = #{state}


	AND f.applier_id = #{applierId}

AND f.filetype_id IS NOT NULL
